INSERT INTO `aula_realizada` VALUES (1,NULL,NULL,'https://api.twilio.com/2010-04-01/Accounts/AC8963db92d979cf31fbdb8df728e70966/Recordings/RE4ac17c7f10a4bb61b5f921a234d17916',NULL,NULL,NULL,5,4,4,'aluno conseguiu entender e reproduzir bem as estruturas, necessita melhorar um pouco o sotaque','Realizada','2017-04-28 10:10:10','2017-04-29 13:10:10','2017-04-29 13:15:10',1,1,1),(2,NULL,NULL,'https://api.twilio.com/2010-04-01/Accounts/AC8963db92d979cf31fbdb8df728e70966/Recordings/RE4ac17c7f10a4bb61b5f921a234d17916',NULL,NULL,NULL,4,3,3,'aluno conseguiu reproduzir a produzir maior parte, problema com o verbo','Realizada','2017-04-30 08:10:10','2017-04-30 18:10:10','2017-04-30 18:15:10',1,2,1),(3,1,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Não atendida','2017-04-30 11:15:10','2017-04-30 18:10:10',NULL,2,1,1),(4,2,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Não atendida','2017-04-30 21:15:10','2017-05-01 18:10:10',NULL,5,2,1);
